    #region DeleteSQL Method
    /// <summary>
    /// Builds the SQL for the DELETE statement, or DELETE stored procedure. You can set the WhereFilter property to selectively delete rows from the table.
    /// </summary>
    /// <returns>string</returns>
    public override string DeleteSQL()
    {
      StringBuilder sb = new StringBuilder(SB_INIT_LENGTH);

      switch (DeleteFilter)
      {
        case DeleteFilters.All:
          if (UseStoredProcs)
          {
            sb.Append("");
          }
          else
          {
            sb.Append(" DELETE FROM GCS.gcsBinaryResource");

          }
          
          break;
        case DeleteFilters.DeleteByPK:
          if (UseStoredProcs)
          {
            sb.Append("GCS.gcsBinaryResource_DeleteByPK");
          }
          else
          {
            sb.Append(" DELETE FROM GCS.gcsBinaryResource WHERE BinaryResourceUid = @BinaryResourceUid And (ConcurrencyValue = @ConcurrencyValue OR ConcurrencyValue is null)");

          }
          
          break;

        case DeleteFilters.Custom:
          sb.Append(DeleteCustom);

          break;          
      }

      if(!UseStoredProcs)
      {
        sb.Append(WhereClauseSQL());
      }
     
      SQL = sb.ToString();

      return SQL;
    }
