    public override string WhereClauseSQL()
    {
      StringBuilder sb = new StringBuilder(SB_INIT_LENGTH);

      switch (WhereFilter)
      {
        case WhereFilters.Custom:
          sb.Append(" " + WhereCustom);

          break;

        case WhereFilters.None:
          //  Do nothing

          break;

        case WhereFilters.PrimaryKey:
          sb.Append(" WHERE BinaryResourceUid = @BinaryResourceUid");

          
          break;
        case WhereFilters.DataType:
          sb.Append(" WHERE DataType = @DataType");

          
          break;
        case WhereFilters.LikeDataType:
          sb.Append(" WHERE DataType LIKE @DataType + '%'");

          
          break;
      }
      
      return sb.ToString();
    }
