    #region SetModificationCommandParameters Method
    /// <summary>
    /// Called to build any parameters prior to submitting an INSERT, UPDATE, or DELETE statement.
    /// </summary>
    protected override void SetModificationCommandParameters()
    {    
      switch (DataModificationAction)
      {
        case PDSADataModificationState.Delete:
          switch (DeleteFilter)
          {
            case DeleteFilters.All:
             CommandObject.Parameters.Clear();
              
              break;
            case DeleteFilters.DeleteByPK:
             CommandObject.Parameters.Clear();
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@BinaryResourceUid", DbType.Guid, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.BinaryResourceUid).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@ConcurrencyValue", DbType.Int16, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.ConcurrencyValue).Value));
              
              break;
          }
          if (this.WhereFilter != WhereFilters.None)
            WhereClauseFillInParameters();

          break;

        case PDSADataModificationState.Insert:
          if (PrimaryKeyType == PDSAPrimaryKeyType.PDSA)
          {
            if (PrimaryKeyGenerate == true)
            {
              //  Get New Primary Key from pdsaTableIds
              PrimaryKeySet(PKGetFromPDSATableIds(PDSALoginName));
            }
          }
          // Clear any Where Filter when doing an INSERT
          this.WhereFilter = WhereFilters.None;

          switch (InsertFilter)
          {
            case InsertFilters.All:
             CommandObject.Parameters.Clear();
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@BinaryResourceUid", DbType.Guid, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.BinaryResourceUid).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@DataType", DbType.String, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.DataType).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@Category", DbType.String, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.Category).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@Tag", DbType.String, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.Tag).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@InsertName", DbType.String, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.InsertName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@InsertDate", DbType.DateTimeOffset, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.InsertDate).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateName", DbType.String, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.UpdateName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateDate", DbType.DateTimeOffset, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.UpdateDate).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@ConcurrencyValue", DbType.Int16, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.ConcurrencyValue).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@BinaryResource", DbType.Binary, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.BinaryResource).Value));
              
              break;
          }

          break;

        case PDSADataModificationState.Update:
          switch (UpdateFilter)
          {
            case UpdateFilters.PrimaryKey:
             CommandObject.Parameters.Clear();
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@BinaryResourceUid", DbType.Guid, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.BinaryResourceUid).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@DataType", DbType.String, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.DataType).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@Category", DbType.String, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.Category).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@Tag", DbType.String, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.Tag).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateName", DbType.String, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.UpdateName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateDate", DbType.DateTimeOffset, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.UpdateDate).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@ConcurrencyValue", DbType.Int16, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.ConcurrencyValue).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@BinaryResource", DbType.Binary, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.BinaryResource).Value));
              
              break;
            case UpdateFilters.UpdateConcurrency:
             CommandObject.Parameters.Clear();
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@ConcurrencyValue", DbType.Int16, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.ConcurrencyValue).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@BinaryResourceUid", DbType.Guid, ValidatorObject.Properties.GetByName(gcsBinaryResourcePDSAValidator.ColumnNames.BinaryResourceUid).Value));
              
              break;
          }
          break;
      }
