    /// <summary>
    /// Delete a record from the table based on the primary key value that is passed in.
    /// </summary>
    /// <param name="entityId">The value of the primary key of the row to delete</param>
    /// <returns>int</returns>
    public int DeleteByPK(Guid entityId)
    {
      DeleteFilter = DeleteFilters.DeleteByPK;
      Entity.EntityId = entityId;      
      
