    #region LoadByPK Method
    /// <summary>
    /// Load a row of data in the Entity properties. Returns a value greater than 0 if it finds the record.
    /// </summary>
    /// <param name="departmentUid">The value of the primary key of the row to load</param>
    /// <returns>Int32</returns>
    public int LoadByPK(Guid departmentUid)
    {
      SelectFilter = SelectFilters.PrimaryKey;
      Entity.DepartmentUid = departmentUid;      

      // Load Data into Properties
      RowsAffected = Load();

      // Reset all Collection Properties
      ResetCollectionProperties();
      
      return RowsAffected;
    }
    #endregion
    
    
    #region Updated Method
    /// <summary>
    /// This method is called after a successful update
    /// </summary>
    protected override void Updated()
    {
      Entity.ConcurrencyValue += Convert.ToInt16(1);
    }
    #endregion

    #region SelectFillInParameters Method
    /// <summary>
    /// Builds the CommandObject.Parameters collection for any SELECT statement parameters.
    /// </summary>
    protected override void SelectFillInParameters()
    {
      switch (SelectFilter)
      {
        case SelectFilters.All:
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@PageNumber", DbType.Int32, Entity.PageNumber));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@PageSize", DbType.Int32, Entity.PageSize));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@SortColumn", DbType.String, Entity.SortColumn));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@DescendingOrder", DbType.Boolean, Entity.DescendingOrder));
         
          break;
        case SelectFilters.ByEntityId:
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@EntityId", DbType.Guid, Entity.EntityId));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@PageNumber", DbType.Int32, Entity.PageNumber));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@PageSize", DbType.Int32, Entity.PageSize));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@SortColumn", DbType.String, Entity.SortColumn));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@DescendingOrder", DbType.Boolean, Entity.DescendingOrder));
         
          break;
        case SelectFilters.ListBox:
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@PageNumber", DbType.Int32, Entity.PageNumber));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@PageSize", DbType.Int32, Entity.PageSize));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@SortColumn", DbType.String, Entity.SortColumn));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@DescendingOrder", DbType.Boolean, Entity.DescendingOrder));
         
          break;
        case SelectFilters.PrimaryKey:
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@DepartmentUid", DbType.Guid, Entity.DepartmentUid));
         
          break;
        case SelectFilters.Search:
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@DepartmentName", DbType.String, Entity.DepartmentName));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@PageNumber", DbType.Int32, Entity.PageNumber));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@PageSize", DbType.Int32, Entity.PageSize));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@SortColumn", DbType.String, Entity.SortColumn));
          CommandObject.Parameters.Add(DataProvider.CreateParameter("@DescendingOrder", DbType.Boolean, Entity.DescendingOrder));
         
          break;
      }

      // Add on paging parameters
      if(UsePaging)
      {
        PagingFillInParameters(Entity.ResultSetRowNumberBegin, Entity.ResultSetRowNumberEnd);
      }
    }
    #endregion


    #region SetModificationCommandParameters Method
    /// <summary>
    /// Called to build any parameters prior to submitting an INSERT, UPDATE, or DELETE statement.
    /// </summary>
    protected override void SetModificationCommandParameters()
    {    
      switch (DataModificationAction)
      {
        case PDSADataModificationState.Delete:
          switch (DeleteFilter)
          {
            case DeleteFilters.DeleteByPK:
             CommandObject.Parameters.Clear();
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@DepartmentUid", DbType.Guid, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.DepartmentUid).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@ConcurrencyValue", DbType.Int16, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.ConcurrencyValue).Value));
              
              break;
          }

          break;

        case PDSADataModificationState.Insert:
          if (PrimaryKeyType == PDSAPrimaryKeyType.PDSA)
          {
            if (PrimaryKeyGenerate == true)
            {
              //  Get New Primary Key from pdsaTableIds
              PrimaryKeySet(PKGetFromPDSATableIds(PDSALoginName));
            }
          }

          switch (InsertFilter)
          {
            case InsertFilters.All:
             CommandObject.Parameters.Clear();
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@DepartmentUid", DbType.Guid, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.DepartmentUid).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@EntityId", DbType.Guid, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.EntityId).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@DepartmentName", DbType.String, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.DepartmentName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@Description", DbType.String, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.Description).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@InsertName", DbType.String, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.InsertName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@InsertDate", DbType.DateTimeOffset, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.InsertDate).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateName", DbType.String, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.UpdateName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateDate", DbType.DateTimeOffset, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.UpdateDate).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@ConcurrencyValue", DbType.Int16, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.ConcurrencyValue).Value));
              
              break;
          }

          break;

        case PDSADataModificationState.Update:
          switch (UpdateFilter)
          {
            case UpdateFilters.PrimaryKey:
             CommandObject.Parameters.Clear();
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@DepartmentUid", DbType.Guid, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.DepartmentUid).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@EntityId", DbType.Guid, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.EntityId).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@DepartmentName", DbType.String, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.DepartmentName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@Description", DbType.String, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.Description).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateName", DbType.String, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.UpdateName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateDate", DbType.DateTimeOffset, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.UpdateDate).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@ConcurrencyValue", DbType.Int16, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.ConcurrencyValue).Value));
              
              break;
            case UpdateFilters.UpdateConcurrency:
             CommandObject.Parameters.Clear();
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@DepartmentUid", DbType.Guid, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.DepartmentUid).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@ConcurrencyValue", DbType.Int16, ValidatorObject.Properties.GetByName(DepartmentPDSAValidator.ColumnNames.ConcurrencyValue).Value));
              
              break;
          }
          break;
      }
    }
    #endregion
