              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@TextData", DbType.String, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.TextData).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@FileStreamData", DbType.Binary, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.FileStreamData).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@BlobData", DbType.Binary, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.BlobData).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@InsertName", DbType.String, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.InsertName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@InsertDate", DbType.DateTimeOffset, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.InsertDate).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateName", DbType.String, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.UpdateName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateDate", DbType.DateTimeOffset, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.UpdateDate).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@ConcurrencyValue", DbType.Int16, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.ConcurrencyValue).Value));
              
              break;
          }

          break;

        case PDSADataModificationState.Update:
          switch (UpdateFilter)
          {
            case UpdateFilters.PrimaryKey:
             CommandObject.Parameters.Clear();
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@Uid", DbType.Guid, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.Uid).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@EntityId", DbType.Guid, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.EntityId).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UniqueTag", DbType.String, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.UniqueTag).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@DataType", DbType.String, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.DataType).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@TextData", DbType.String, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.TextData).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@FileStreamData", DbType.Binary, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.FileStreamData).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@BlobData", DbType.Binary, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.BlobData).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateName", DbType.String, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.UpdateName).Value));
              CommandObject.Parameters.Add(DataProvider.CreateParameter( 
                  "@UpdateDate", DbType.DateTimeOffset, ValidatorObject.Properties.GetByName(gcsLargeObjectStoragePDSAValidator.ColumnNames.UpdateDate).Value));
